import React from "react";
import './styles/home.style.css'
import './styles/common.style.css'
import "bootstrap/dist/css/bootstrap.min.css";
import authService from '../services/auth.service'
import configs from '../config'
import axios from 'axios'
import NavBar from "../components/navbar.component";
import { Button, Modal } from "react-bootstrap";

class Home extends React.Component {

     constructor(props) {
          super(props)

          // check if user did not sign in
          let user = authService.getCurrentUser();
          if (!user) {
               this.state = {
                    broadcasters: {},
                    user: null,
                    isPrivate: false,
                    show: false
               }
          }
          else {
               this.state = {
                    broadcasters: {},
                    user: JSON.parse(user),
                    isPrivate: false,
                    show: false
               }
          }
     }

     handleClose = () => {
          this.setState({
               show: false
          })
     }

     handleShow = () => {
          this.setState({
               show: true
          })
     }

     componentDidMount() {
          this.GetBroadcasters()
     }

     GetBroadcasters = () => {
          axios.get(`${configs.API_URL}/broadcaster/list`)
               .then(res => {
                    this.setState({
                         broadcasters: res.data
                    })
                    console.log(this.state.broadcasters)
               })
     }



     render() {
          return (
               <div>
                    <NavBar user={this.state.user} history={this.props.history} handleSwitch={this.handleSwitch} isStreaming={false} />
                    <table className="table">
                         <thead className="bg-primary text-light">
                              <tr>
                                   <th scope="col"><h1>On Stream</h1></th>
                              </tr>
                         </thead>
                         <tbody>
                              {/* {Object.keys(this.state.broadcasters).length === 0 ? <tr><td>No streamer online</td></tr> : (
                                   Object.keys(this.state.broadcasters).map((key, i) => {
                                        let link = `/watch/${this.state.broadcasters[key].id}`

                                        if (this.state.broadcasters[key].mode === 'private') {
                                             return (
                                                  <tr key={i}>
                                                       <td><a href={link}>{this.state.broadcasters[key].name}</a> <span class="badge badge-danger">private</span></td>
                                                  </tr>
                                             )
                                        }
                                        else {
                                             return (
                                                  <tr key={i}>
                                                       <td><a href={link}>{this.state.broadcasters[key].name}</a> <span class="badge badge-success">public</span></td>
                                                  </tr>
                                             )
                                        }
                                   }
                                   )
                              )} */}
                              <Button variant="link" onClick={this.handleShow}>
                                   Launch demo modal
                                   &nsbp;
                                   <span class="badge badge-success">public</span>
                              </Button>
                         </tbody>
                    </table>

                    <Modal show={this.state.show} onHide={this.handleClose}>
                         <Modal.Header closeButton>
                              <Modal.Title>Room authentication</Modal.Title>
                         </Modal.Header>
                         <Modal.Body>
                              <div>Enter password</div>
                              <input type='text'/>
                         </Modal.Body>
                         <Modal.Footer>
                              <Button variant="secondary" onClick={this.handleClose}>
                                   Close
                              </Button>
                              <Button variant="primary" onClick={this.handleClose}>
                                   Submit
                              </Button>
                         </Modal.Footer>
                    </Modal>
               </div>
          )
     }
}

export default Home;
