import React from "react";
import Video from "../components/video.component";

class Home extends React.Component {
     constructor() {
          super()
          this.state = {
               live_container: <div>
                    <input type='text' id='name'></input>
                    <button id='control-btn' onClick={this.StartStream}>Stream now !</button>
                    <div id='stream-screen'>
                         <Video/>
                    </div>
               </div>
          }
     }

     StopStreaming = () => {

     }

     StartStream = () => {
          this.setState({
               live_container: <div>
               <input type='text' id='name'></input>
               <button id='control-btn' onClick={this.StopStreaming}>Stream now !</button>
               <div id='stream-screen'>
                    <Video/>
               </div>
          </div>
          })
     }

     render() {
          return (
               <div>
                    <h1>Streamer online</h1>
                    <ul>

                    </ul>
                    <hr></hr>
                    {this.state.live_container}
               </div>
          )
     }
}

export default Home;
