import React from "react";
import Video from "../components/video.component";
import io from 'socket.io-client'

const peerConnections = {};
const config = {
     iceServers: [
          {
               urls: ["stun:stun.l.google.com:19302"]
          }
     ]
};

const socket = io('localhost:5000');
const video = document.querySelector("video");

// Media contrains
const constraints = {
     video: { facingMode: "user" },
     // Uncomment to enable audio
     audio: true,
};

class Home extends React.Component {
     constructor() {
          super()
          this.state = {
               live_container: <div>
                    <input type='text' id='name'></input>&nbsp;
                    <button id='control-btn' onClick={this.StartStream}>Stream now !</button>
               </div>
          }
     }



     StopStreaming = () => {
          alert(123)
     }

     StartStream = () => {
          let name = document.getElementById('name').value
          this.setState({
               live_container: <div>
                    User: {name}&nbsp;
                    <button id='control-btn' onClick={this.StopStreaming}>Stop streaming</button>
                    <div id='stream-screen' style={{ margin: '20px' }}>
                         <Video />
                    </div>
               </div>
          })

          navigator.mediaDevices
               .getUserMedia(constraints)
               .then(stream => {
                    video.srcObject = stream;
                    socket.emit("broadcaster");
               })
               .catch(err => console.error(err))

          socket.on("watcher", id => {
               const peerConnection = new RTCPeerConnection(config);
               peerConnections[id] = peerConnection;

               let stream = video.srcObject;
               stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));

               peerConnection.onicecandidate = event => {
                    if (event.candidate) {
                         socket.emit("candidate", id, event.candidate);
                    }
               };

               peerConnection
                    .createOffer()
                    .then(sdp => peerConnection.setLocalDescription(sdp))
                    .then(() => {
                         socket.emit("offer", id, peerConnection.localDescription);
                    });
          });

          socket.on("answer", (id, description) => {
               peerConnections[id].setRemoteDescription(description);
          });

          socket.on("candidate", (id, candidate) => {
               peerConnections[id].addIceCandidate(new RTCIceCandidate(candidate));
          });
     }

     render() {
          return (
               <div>
                    <h1>Streamer online</h1>
                    <ul>

                    </ul>
                    <hr></hr>
                    {this.state.live_container}
               </div>
          )
     }
}

export default Home;
