import React from "react";
import 'styles/home.style.css'
import configs from '../config'
import axios from 'axios'

class Home extends React.Component {

     constructor() {
          super()
          this.state = {
               broadcasters: []
          }
     }

     componentDidMount() {
          this.GetBroadCasters()
     }

     GetBroadCasters = () => {
          axios.get(`http://${configs.API_URL}/broadcaster/list`)
               .then(res => {
                    this.setState({
                         broadcasters: res.data
                    })
               })
     }

     render() {
          return (
               <div>
                    <h1>Streamer online</h1>
                    <table class="table">
                         <thead class="thead-dark">
                              <tr>
                                   <th scope="col">#</th>
                                   <th scope="col">Handle</th>
                              </tr>
                         </thead>
                         <tbody>
                              <tr>
                                   <td>Mark</td>
                                   <td>@mdo</td>
                              </tr>
                              <tr>
                                   <td>Thornton</td>
                                   <td>@fat</td>
                              </tr>
                              <tr>
                                   <td>the Bird</td>
                                   <td>@twitter</td>
                              </tr>
                         </tbody>
                    </table>

                    <ul>
                         {this.state.broadcasters.map((bc, i) => {
                              let link = `/watch/${bc}`
                              return <li key={i}><a href={link}>{bc}</a></li>
                         })}
                    </ul>
               </div>
          )
     }
}

export default Home;
