import React from "react";
import Video from "../components/video.component";

class Home extends React.Component {
     constructor() {
          super()
          this.state = {
               video : null
          }
     }

     StartStream = () => {
          this.setState({
               video : <Video/>
          })
          document.getElementById('control-btn').innerHTML = 'Stop streaming'
     }

     render() {
          return (
               <div>
                    <h1>Streamer online</h1>
                    <ul>

                    </ul>
                    <hr></hr>
                    <div>
                         <input type='text' id='name'></input>
                         <button id='control-btn' onClick={this.StartStream}>Stream now !</button>
                         <div id='stream-screen'>
                              {this.state.video}
                         </div>
                    </div>
               </div>
          )
     }
}

export default Home;
