import React from "react";
import Video from "../components/video.component";

class Home extends React.Component {
     constructor() {
          super()
          this.state = {
               live_container: <div>
                    <input type='text' id='name'></input>&nbsp;
                    <button id='control-btn' onClick={this.StartStream}>Stream now !</button>
               </div>
          }
     }

     StopStreaming = () => {

     }

     StartStream = () => {
          let name = document.getElementById('name').value
          this.setState({
               live_container: <div>
                    User: {name}&nbsp;
                    <button id='control-btn' onClick={this.StopStreaming}>Stop streaming</button>
                    <div id='stream-screen' style={{margin:'20px'}}>
                         <Video />
                    </div>
               </div>
          })
     }

     render() {
          return (
               <div>
                    <h1>Streamer online</h1>
                    <ul>

                    </ul>
                    <hr></hr>
                    {this.state.live_container}
               </div>
          )
     }
}

export default Home;
